SELECT  CD_LOTE, 
		NR_LOTE, 
		QTDE_LOTE 
FROM PDV_VD_IT_LOTE LOTE
WHERE LOTE.CD_EMP  = #{cdEmp}
AND LOTE.CD_FILIAL = #{cdFilial}
AND LOTE.CD_VD     = #{cdVd}
AND LOTE.CD_IT     = #{cdIt}


	SELECT  
