select.if:
SELECT 

					cupom.cd_emp				,
					cupom.cd_filial             ,
					cupom.cd_cx,
					cupom.cd_ctr,
				   cupom.hash_protocolo         			
				   from pdv_cupom_nao_fiscal cupom
				   where cupom.cd_emp = #{cdEmp}
				   AND   cupom.cd_filial = #{cdFilial}
				   AND   cupom.cd_cx = #{cdCx}
				   AND   cupom.cd_ctr = #{cdCtr}
